import React, { Component } from 'react';
import Title from './Title.js';
import NoteView from './NoteView.js';
import Note from './Note.js';
import CreateNote from './CreateNote.js';
import './App.css';

const dummy = [{
  noteContents: [{
    title: "NoteNote1",
    content: "Enter note he"
  },
  {
    title: "Note 2",
    content: "Enter note here"
  }
  ]
}
];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      noteContents: [{
        title: "NoteNote1",
        content: "Enter note he"
      },
      {
        title: "Note 2",
        content: "Enter note here"
      }
      ]
    }
  }

  createNewNote = () => {
    this.setState(prevState => {
      return {
        noteContents: prevState.noteContents.push({
          title: "Note 3".concat(prevState.noteContents.length + 1),
          content: "Enter note here"
        })
      };
    });
  }

  render() {
    return (
      <div className="App" >
        <Title />
        {dummy.map(note => {
          return (
            <Note title={note.title} content={note.content} className="note" />
          );
        })}
        <CreateNote buttonPass={this.createNewNote} />
      </div>
    );
  }
}

export default App;
