import React, { Component } from 'react';
import Title from './Title.js';
import NoteView from './NoteView.js';
import Note from './Note.js';
import CreateNote from './CreateNote.js';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numNotes: 2,
      noteContents: [{
        title: "NoteNote1",
        content: "Enter note he"
      },
      {
        title: "Note 2",
        content: "Enter note here"
      }
      ]
    }
  }

  createNewNote = () => {
    this.setState(prevState => {
      return {
        numNotes: prevState.numNotes + 1,
        noteContents: prevState.noteContents.push({
          title: "Note " + (prevState.numNotes + 1),
          content: "Enter note here"
        })
      };
    });
  }

  render() {
    return (
      <div className="App" >
        <Title />
        {this.state.noteContents.map(note => {
          return (
            <Note title={note.title} content={note.content} className="note" />
          );
        })}
        <CreateNote buttonPass={this.createNewNote} />
      </div>
    );
  }
}

export default App;
