import React, { Component } from 'react';
import Title from './Title.js';
import NoteView from './NoteView.js';
import Note from './Note.js';
import CreateNote from './CreateNote.js';
import './App.css';

notes = [];

let numNotes = 0;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numNotes: 1,
      noteContents: [{
        title: "Note 1",
        content: "Enter note here"
      }]
    }
  }

  createNewNote = () => {
    this.setState(prevState => {
      return {
        numNotes: prevState.numNotes + 1,
        noteContents: prevState.noteContents.push({
          title: "Note " + (prevState.numNotes + 1),
          content: "Enter note here"
        })
      };
    });
  }

  render() {
    return (
      <div className="App" >
        <Title />
        <Note />
        <Note />
        <Note />
        <Note />
        <CreateNote />
      </div>
    );
  }
}

export default App;
